    def part2(input: String): Int = {
        def count(lastSum: Int, firstElemIt: Iterator[Int], lastElemIt: Iterator[Int]): Int = {
            if (!lastElemIt.hasNext) 0
            else {
                val firstElem = firstElemIt.next
                val lastElem = lastElemIt.next
                val newSum = lastSum - firstElem + lastElem
                (lastSum < newSum) + count(newSum, firstElemIt, lastElemIt)
            }
        }
        val inputArray = input.split("\n").map(_.toInt)
        val first = inputArray.iterator
        val it = inputArray.iterator
        count(it.next + it.next + it.next, first, it)
    }
